css:
.card-sp{
    border: 1px solid #00000020; border-radius: 0.55rem;
}
.card-sp .card-header{
    background-color: #f2f2f2; padding: 7px 8px; font-size: 15px; border-top-left-radius: 0.55rem; border-top-right-radius: 0.55rem;
}
.card-sp .card-body{
    padding: 7px 8px;
}
th {
    background-color: #eee !important;
    color: #000000;
    font-weight: bold;
}
.print-format td{
    padding: 6px !important;
}
